-----------------------------------------------------------
v#	Author		Date			Description
#1
*/

CREATE  Procedure [prcAppConfig_Update]
(
@name nVarchar(50),
@value nvarchar(4000)
)

As
if (not exists(select name
from tblAppConfig
where name=@name))
begin
Select
1 As ''ErrorNumber'',
''Record not found'' As ''ErrorMessage''
end
else
begin
Update tblAppConfig
set value =@value
where name=@name

select	0 As ''ErrorNumber'',
'''' As ''ErrorMessage''

end




' 
END
