(
@courseID,
@name,
@description,
@active,
@userID,
GetUTCDate(),
@userID,
GetUTCDate()
)

-- Set the error message to successfull
Set @intErrorNumber = 0
Set @strErrorMessage = @@identity

-- Finalise the procedure
Goto Finalise



Finalise:
If(@intErrorNumber > 0)
Begin
Rollback Transaction
Select
@intErrorNumber As ''ErrorNumber'',
@strErrorMessage As ''ErrorMessage''
End
Else
Begin
Commit Transaction
Select
@intErrorNumber As ''ErrorNumber'',
@strErrorMessage As ''ErrorMessage''
End
' 
END
