CREATE PROCEDURE [dbo].[prcPolicy_GetUserAndPoliciesForAdmins]
@admin_ids varchar(8000),
@policy_ids varchar(8000),
@unit_ids varchar(max),
@accepted varchar (20),
@acceptedDateFrom 		datetime,
@acceptedDateTo 		datetime,
@OrgID int
AS
BEGIN
