CREATE  Procedure [prcUnit_SearchUsers]
(
@unitID Int,
@firstName nvarchar(50),
@lastName nvarchar(50),
@userTypeID int =null,
@adminUserID int,
@includeInactiveUsers bit = 0
)

As

set nocount off

--Check data
if (@userTypeID is null or @userTypeID<=0)
begin
select @userTypeID = UserTypeID
from tblUser
where userID = @adminUserID
end

if @firstName is null
set @firstName=''''
if @lastName is null
set @lastName=''''

if (@includeInactiveUsers=0)
Begin
Select 	UserID,
UserName,
FirstName,
LastName,
Email,
Active
From tblUser
Where UnitID = @unitID
and firstName like ''%''+ @firstName +''%''
and lastName like ''%'' + @lastName +''%''
and UserTypeID >=@userTypeID
and Active = 1
order by LastName
End
Else
Begin
Select 	UserID,
UserName,
FirstName,
LastName,
Email,
Active
From tblUser
Where UnitID = @unitID
and firstName like ''%''+ @firstName +''%''
and lastName like ''%'' + @lastName +''%''
and UserTypeID >=@userTypeID
order by LastName
End
' 
END
